import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  fetchDetail,
  isLoadingDetail,
  completeDetail,
  errorDetail
} from '../redux/slices/detail';

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const note = useSelector(completeDetail);
  const isLoading = useSelector(isLoadingDetail);
  const error = useSelector(errorDetail);

  useEffect(() => {
    dispatch(fetchDetail(id));
  }, [id]);

  return (
    <div>
      <h1>Note: {id}</h1>
      {isLoading && <div className='text-center'>Cargando...</div>}

      {note.content}
      <Card>
        <Card.Header as="h5">Featured</Card.Header>
        <Card.Body>
          <Card.Title>Special title treatment</Card.Title>
          <Card.Text>
            With supporting text below as a natural lead-in to additional content.
          </Card.Text>
          <Button variant="primary">Go somewhere</Button>
        </Card.Body>
      </Card>

      {!error && <div>Hubo un error</div>}
    </div>
  );
};

export default Detail;
