import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  fetchDetail,
  isLoadingDetail,
  completeDetail,
  errorDetail
} from '../redux/slices/detail';

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const note = useSelector(completeDetail);
  const isLoading = useSelector(isLoadingDetail);
  const error = useSelector(errorDetail);

  useEffect(() => {
    dispatch(fetchDetail(id));
  }, [id]);

  return (
    <div>
      <h1>Note: {id}</h1>
      {isLoading && <div className='text-center'>Cargando...</div>}
      {note.content}
      {!error && <div>Hubo un error</div>}
    </div>
  );
};

export default Detail;
